import React , { Component } from 'react';
import App from './App.js';
import robots from './robots.js';



  class Web extends Component {
      constructor(){
          super()
          this.state = {
              searchField : ''
          }
      };

      onSearchChange = (event) => {
          this.setState({ searchField : event.target.value});
      };

    render(){

        const filteredbots = robots.filter(robots =>{
            return robots.name.toLowerCase().includes(this.state.searchField.toLowerCase());
        });

        const cards = filteredbots.map(( user, i) => {
            return (
              <App 
              id={filteredbots[i].id} 
              name={filteredbots[i].name} 
              email={filteredbots[i].email}
              />);
          });

        return(
            <div className='tc'>
            <h1>Robo Friends</h1>
            <div className='pa2'>
                <input className='pa3 ba b--green bg-lightest-blue' type='search' onChange={this.onSearchChange}/>
            </div>
            {cards}
          </div>
        );
    }

}

export default Web;
